// Function to decode HTML entities
function decodeHtmlEntities(str) {
    return str.replace(/&#(\d+);/g, (_, dec) => String.fromCharCode(dec))  // decimal entities
              .replace(/&#x([0-9a-fA-F]+);/g, (_, hex) => String.fromCharCode(parseInt(hex,16))) // hex entities
              .replace(/&([a-z]+);/gi, (_, name) => {
                  const entities = {nbsp: ' ', amp: '&', lt: '<', gt: '>', quot: '"', apos: '\''};
                  return entities[name.toLowerCase()] || '';
              });
}

// Dynamic HTML input
let text = decodeHtmlEntities($input.first().json.textAsHtml);

// Remove all HTML tags
text = text.replace(/<[^>]*>/g, '\n');

// Normalize line breaks
text = text.replace(/\r\n|\r/g, '\n').replace(/\n+/g, '\n').trim();

// **Remove emojis completely**
text = text.replace(/[\p{Emoji_Presentation}\p{Extended_Pictographic}]/gu, '');

// Extract Action Items section
let actionMatch = text.match(/Action Items ([\s\S]*?)(\nMeeting Summary|$)/i);
if (!actionMatch) return [];

// Get plain lines
let actionText = actionMatch[1].trim();
let lines = actionText.split('\n').map(l => l.trim()).filter(l => l);

// Return cleaned action list
return [{ json: { actionList: lines.join('\n') } }];

