// Get the tasks array from the previous node output
const tasks = $input.all();

// Format each task into a readable structure
const formattedTasks = tasks.map((task, index) => {
  const taskData = task.json;

  // Extract description and assignee from notes field
  let description = taskData.notes || 'No description';
  let assignee = 'Unassigned';
  let employeeInList = 'Unknown';
  let userId = 'N/A';
  let notes = '';

  // Parse the notes field to extract structured information
  if (description) {
    // Extract assignee (look for "Assignee: " line)
    const assigneeMatch = description.match(/Assignee:\s*([^\n]+)/);
    if (assigneeMatch) {
      assignee = assigneeMatch[1].trim();
    }

    // Extract employee in list status
    const employeeMatch = description.match(/Employee in list:\s*([^\n]+)/);
    if (employeeMatch) {
      employeeInList = employeeMatch[1].trim();
    }

    // Extract user ID
    const userIdMatch = description.match(/User ID:\s*([^\n]+)/);
    if (userIdMatch) {
      userId = userIdMatch[1].trim();
    }

    // Extract note if exists
    const noteMatch = description.match(/Note:\s*([^\n]+)/);
    if (noteMatch) {
      notes = noteMatch[1].trim();
    }

    // Clean description to only show the actual task description (before "Assignee:")
    const descParts = description.split('\n\nAssignee:');
    description = descParts[0].trim();
  }

  return {
    taskNumber: index + 1,
    title: taskData.title || 'No title',
    status: taskData.status || 'Unknown',
    created: taskData.updated ? new Date(taskData.updated).toLocaleString() : 'N/A',
    taskId: taskData.id || 'N/A',
    viewLink: taskData.webViewLink || 'N/A',
    description: description,
    assignee: assignee,
    employeeInList: employeeInList,
    userId: userId,
    notes: notes,
    dueDate: taskData.due ? new Date(taskData.due).toLocaleDateString() : 'No due date'
  };
});

// Create a summary
const summary = {
  totalTasks: formattedTasks.length,
  tasksCreated: formattedTasks.filter(t => t.status === 'needsAction').length,
  tasksWithValidAssignee: formattedTasks.filter(t => t.employeeInList === 'Yes').length,
  tasksWithInvalidAssignee: formattedTasks.filter(t => t.employeeInList === 'No').length,
  timestamp: new Date().toLocaleString()
};

// Create readable text output
let readableOutput = `üìã TASK CREATION SUMMARY\n`;
readableOutput += `${'='.repeat(60)}\n\n`;
readableOutput += `Total Tasks Created: ${summary.totalTasks}\n`;
readableOutput += `Tasks with Valid Assignee: ${summary.tasksWithValidAssignee}\n`;
readableOutput += `Tasks with Invalid Assignee: ${summary.tasksWithInvalidAssignee}\n`;
readableOutput += `Timestamp: ${summary.timestamp}\n\n`;
readableOutput += `${'='.repeat(60)}\n`;

formattedTasks.forEach(task => {
  // Add warning emoji for tasks with invalid assignees
  const warningIcon = task.employeeInList === 'No' ? '‚ö†Ô∏è ' : '';

  readableOutput += `\n${warningIcon}üîπ Task ${task.taskNumber}\n`;
  readableOutput += `   Title: ${task.title}\n`;
  readableOutput += `   Description: ${task.description}\n`;
  readableOutput += `   Assignee: ${task.assignee}`;

  if (task.employeeInList === 'No') {
    readableOutput += ` ‚ö†Ô∏è NOT FOUND IN EMPLOYEE LIST`;
  } else if (task.employeeInList === 'Yes') {
    readableOutput += ` ‚úÖ`;
  }

  readableOutput += `\n`;
  readableOutput += `   User ID: ${task.userId}\n`;
  readableOutput += `   Status: ${task.status}\n`;
  readableOutput += `   Created: ${task.created}\n`;
  readableOutput += `   Due Date: ${task.dueDate}\n`;

  if (task.notes) {
    readableOutput += `   Note: ${task.notes}\n`;
  }

  readableOutput += `   Task ID: ${task.taskId}\n`;
  readableOutput += `   View Link: ${task.viewLink}\n`;
  readableOutput += `   ${'-'.repeat(58)}\n`;
});

// Add footer with action items if there are invalid assignees
if (summary.tasksWithInvalidAssignee > 0) {
  readableOutput += `\n‚ö†Ô∏è ACTION REQUIRED:\n`;
  readableOutput += `${summary.tasksWithInvalidAssignee} task(s) have assignees not found in the employee list.\n`;
  readableOutput += `Please review and update these assignments.\n`;
}

// Return both structured data and readable text
return [
  {
    json: {
      summary,
      tasks: formattedTasks,
      readableOutput
    }
  }
];

