const inputItems = $input.all();

const results = [];

inputItems.forEach((item, index) => {
    try {
        const json = item.json;

        // Get fields from Google Sheets (using correct column names)
        let title = json.Title || json.title || '';
        let htmlLink = json.HTML_Link || json['HTML_Link'] || '';
        let browserLink = json.Browser_Link || json['Browser_Link'] || '';
        let status = json.Status || json.status || '';
        let tag = json.Tag || json.tag || '';
        let imageLink = json['Image Link'] || '';
        let aiGeneratedImage = json['AI Generated Image'] || '';

        // Remove leading single quote (used for Google Sheets text formatting)
        if (typeof title === 'string' && title.startsWith("'")) {
            title = title.substring(1);
        }
        if (typeof htmlLink === 'string' && htmlLink.startsWith("'")) {
            htmlLink = htmlLink.substring(1);
        }
        if (typeof browserLink === 'string' && browserLink.startsWith("'")) {
            browserLink = browserLink.substring(1);
        }
        if (typeof tag === 'string' && tag.startsWith("'")) {
            tag = tag.substring(1);
        }
        if (typeof imageLink === 'string' && imageLink.startsWith("'")) {
            imageLink = imageLink.substring(1);
        }
        if (typeof aiGeneratedImage === 'string' && aiGeneratedImage.startsWith("'")) {
            aiGeneratedImage = aiGeneratedImage.substring(1);
        }

        // Determine which image to use (prioritize Image Link, then AI Generated Image)
        let featuredImage = '';
        let imageSource = '';

        // Trim and check for non-empty values
        const trimmedImageLink = String(imageLink || '').trim();
        const trimmedAiImage = String(aiGeneratedImage || '').trim();

        if (trimmedImageLink.length > 0) {
            featuredImage = trimmedImageLink;
            imageSource = 'image_link';
        } else if (trimmedAiImage.length > 0) {
            featuredImage = trimmedAiImage;
            imageSource = 'ai_generated';
        }

        let htmlContent = null;
        let htmlExtracted = false;

        // Check if HTML_Link is a data URL (after removing the quote prefix)
        if (htmlLink && typeof htmlLink === 'string' && htmlLink.trim().startsWith('data:text/html')) {
            try {
                // Extract base64 part from data URL
                const base64Match = htmlLink.match(/base64,(.+)$/);
                if (base64Match && base64Match[1]) {
                    // Decode base64 to get HTML
                    htmlContent = Buffer.from(base64Match[1], 'base64').toString('utf-8');
                    htmlExtracted = true;
                } else {
                    // Try direct base64 decode
                    const parts = htmlLink.split(',');
                    if (parts.length > 1) {
                        const base64Part = parts.slice(1).join(',');
                        htmlContent = Buffer.from(base64Part, 'base64').toString('utf-8');
                        htmlExtracted = true;
                    }
                }
            } catch (error) {
                htmlContent = null;
                htmlExtracted = false;
                console.error(`Failed to decode base64: ${error.message}`);
            }
        }

        // Validate extracted HTML
        if (htmlContent && htmlExtracted) {
            const trimmed = htmlContent.trim();
            if (!trimmed.toLowerCase().startsWith('<!doctype') && 
                !trimmed.toLowerCase().startsWith('<html') && 
                !trimmed.startsWith('<')) {
                htmlExtracted = false;
            }
        }

        // Return extracted data
        results.push({
            json: {
                title: title,
                status: status,
                tag: tag,
                html_link: htmlLink,
                browser_link: browserLink,
                featured_image: featuredImage,
                image_source: imageSource,
                html_content: htmlContent,
                html_extracted: htmlExtracted,
                ready_for_database: htmlExtracted && htmlContent && htmlContent.length > 0
            }
        });

    } catch (error) {
        results.push({
            json: {
                title: '',
                status: '',
                tag: '',
                html_link: '',
                browser_link: '',
                featured_image: '',
                image_source: '',
                html_content: null,
                html_extracted: false,
                ready_for_database: false,
                error: error.message
            }
        });
    }
});

return results;

const inputItems = $input.all();

const results = [];

inputItems.forEach((item) => {
    const json = item.json;

    // Get title - prefer lowercase, fallback to Title (with quote stripped)
    let title = json.title || '';
    if (!title && json.Title) {
        title = json.Title;
        if (typeof title === 'string' && title.startsWith("'")) {
            title = title.substring(1);
        }
    }

    // Get link - prefer lowercase
    let link = json.link || '';
    if (!link && json.Link) {
        link = json.Link;
        if (typeof link === 'string' && link.startsWith("'")) {
            link = link.substring(1);
        }
    }

    // Extract HTML content from base64 link (since html_content is missing)
    let content = json.html_content || json.content || '';
    if (!content && link && typeof link === 'string' && link.includes('base64,')) {
        try {
            const base64Match = link.match(/base64,(.+)$/);
            if (base64Match && base64Match[1]) {
                content = Buffer.from(base64Match[1], 'base64').toString('utf-8');
            }
        } catch (e) {
            content = '';
        }
    }

    // Build output for MongoDB
    const outputData = {
        title: title,
        content: content,
        category: json.category || json.tag || '',
        image: json.image || json.featured_image || '',
        status: json.status || '',
        html_extracted: content.length > 0,
        ready_for_database: content.length > 0,
        created_at: json.created_at || new Date().toISOString()
    };

    results.push({
        json: outputData
    });
});

return results;

const inputItems = $input.all();

const results = [];

inputItems.forEach((item) => {
    const json = item.json;

    // Extract description from AI response
    let description = '';

    // AI response with "text" field (your current format)
    if (json.text) {
        description = json.text.trim();
    }
    // OpenAI node output format
    else if (json.message && json.message.content) {
        description = json.message.content.trim();
    }
    // HTTP Request to OpenAI API format
    else if (json.choices && json.choices[0]) {
        description = json.choices[0].message.content.trim();
    }
    // Fallback to title
    else {
        description = json.title || 'Blog post';
    }

    // Clean up description (remove quotes if AI added them)
    description = description.replace(/^["']|["']$/g, '').trim();

    // Ensure max length
    if (description.length > 160) {
        description = description.substring(0, 157) + '...';
    }

    results.push({
        json: {
            description: description
        }
    });
});

return results;

